mapper.select:
SELECT * FROM sys_res_role


    ${whereSql}



  ORDER BY ${orderBySql}


  ORDER BY id_ DESC

mapper.update:
UPDATE sys_res_role
SET system_id_=#{systemId,jdbcType=VARCHAR},
    res_id_=#{resId,jdbcType=VARCHAR},
    role_id_=#{roleId,jdbcType=VARCHAR}
WHERE id_ = #{id}

mapper.delete:
DELETE
FROM sys_res_role
WHERE role_id_ = #{roleId}
  AND system_id_ = #{systemId}
