mapper.update:
UPDATE sys_user
SET password = #{password}
WHERE id = #{userId}

UPDATE sys_user
SET email = #{email}
WHERE id = #{userId}

UPDATE sys_user
SET telephone = #{telephone}
WHERE id = #{userId}

